mapper.update:
   update tbl_written_statistic set psychology_pass_rate = (select count(id) / (select count(id) from tbl_written_score where exam_date = ${year}) rate
    from tbl_written_score
where psychology_pass_rate > ${psychologyPassScore}
  and exam_date = ${year}) where year=${year}

update tbl_written_statistic set ethic_pass_rate = (select count(id) / (select count(id) from tbl_written_score where exam_date = ${year}) rate
from tbl_written_score
where ethic_pass_rate > ${ethicPassScore}
  and exam_date = ${year}) where year=${year}
